mapper.update:
UPDATE roomtype
SET roomnumber= roomnumber - 1
WHERE roomtypeno = #{roomtypeno}

UPDATE roomtype
SET roomnumber= roomnumber + 1
WHERE roomtypeno = #{roomtypeno}

UPDATE roomtype
set roomprice = #{roomprice}
WHERE roomtypeno = #{roomtypeno}

mapper.select:
SELECT roomtypeno
from roomtype
WHERE roomtypename = #{roomtypename}
